import fastify, { FastifyInstance } from 'fastify';
import questionRoutes from './apiServices/question/question.route';
import env from './config/env';

export default class Server {
  public server: FastifyInstance;

  constructor() {
    this.server = fastify({
      logger: {
        level: 'info',
        transport: {
          target: 'pino-pretty',
        },
      },
    });
    this.setup();
  }

  public listen() {
    this.server.listen({ port: env.port }, async (err, address) => {
      if (err) {
        console.error(err);
        process.exit(1);
      }
      console.log(`Server listening at ${address}`);
    });
  }

  private async setup() {
    this.setRouter();
  }

  private async setRouter() {
    const options = { prefix: '/v1', logLevel: 'debug' };
    await this.server.register(questionRoutes, options);
  }
}

import type { Static } from '@sinclair/typebox';
import type { FastifyInstance } from 'fastify';
import validation from './question.validation';
import controller from './question.controller';

export default (fastify: FastifyInstance, _: any, done: () => void) => {

  
  fastify.post<{ Body: Static<typeof validation.createCategoryBody> }>(
    '/category',
    {
      schema: {
        body: validation.createCategoryBody,
      },
    },
    controller.createCategory
  );

  fastify.post<{ Body: Static<typeof validation.createQuestionBody> }>(
    '/question',
    {
      schema: {
        body: validation.createQuestionBody,
      },
    },
    controller.createQuestion
  );

  fastify.post<{ Body: Static<typeof validation.createAnswerBody> }>(
    '/answer',
    {
      schema: {
        body: validation.createAnswerBody,
      },
    },
    controller.createAnswer
  );

  fastify.get<{ Querystring: Static<typeof validation.getCategoriesPaginatedQuery> }>(
    '/category',
    {
      schema: {
        querystring: validation.getCategoriesPaginatedQuery,
      },
    },
    controller.getCategoriesPaginated
  );
  done();
};

import { Type } from "@sinclair/typebox";

const createCategoryBody = Type.Object({
  name: Type.String(),
});

const createQuestionBody = Type.Object({
  statement: Type.String(),
  categoryId: Type.Number(),
});

const createAnswerBody = Type.Object({
  content: Type.String(),
  questionId: Type.Number(),
});

const getCategoriesPaginatedQuery = Type.Object({
  name: Type.Optional(Type.String()),
  page: Type.Number({ minimum: 1 }),
  size: Type.Number({ minimum: 2 }),
  skip: Type.Number({ minimum: 2 })
});

export default { 
    createCategoryBody,
    createQuestionBody,
    createAnswerBody,
    getCategoriesPaginatedQuery,
}

import type { FastifyReply, FastifyRequest } from 'fastify';
import type { Static } from '@sinclair/typebox';
import QuestionService from './question.service';
import type validation from './question.validation';

const questionService = new QuestionService();

const createCategory = async function (request: FastifyRequest<{ Body: Static<typeof validation.createCategoryBody> }>, reply: FastifyReply) {
  const category = await questionService.createCategory(request.body);
  reply.send(category);
};

const createQuestion = async function (request: FastifyRequest<{ Body: Static<typeof validation.createQuestionBody> }>, reply: FastifyReply) {
  const { categoryId, statement } = request.body;
  const category = await questionService.createQuestion(categoryId, { statement });
  reply.send(category);
};

const createAnswer = async function (request: FastifyRequest<{ Body: Static<typeof validation.createAnswerBody> }>, reply: FastifyReply) {
  const { questionId, content } = request.body;
  const category = await questionService.createAnswer(questionId, { content });
  reply.send(category);
};

const getCategoriesPaginated = async (request: FastifyRequest<{ Querystring: Static<typeof validation.getCategoriesPaginatedQuery> }>, reply: FastifyReply) =>  {
  const { name } = request.query;
  const category = await questionService.filterPaginateCategory(request.query, name);
  reply.send(category);
}

export default {
  createCategory,
  createQuestion,
  createAnswer,
  getCategoriesPaginated,
}

import prisma from '../../config/prisma';
import type AnswerType from './types/answer.type';
import type { CategoryType } from './types/category.type';
import type QuestionType from './types/question.type';

export interface PaginateType<T> {
  page: number;
  next: number | null;
  previous: number | null;
  results: T;
  totalPages: number;
  countResults: number;
}

export interface PaginateOptions {
  page: number;
  size: number;
  skip: number;
}

export default class QuestionService {
  protected categoryDB = prisma.category;
  protected questionDB = prisma.question;
  protected answerDB = prisma.answer;

  async createCategory(category: CategoryType) {
    return this.categoryDB.create({
      data: category,
    });
  }

  async createQuestion(categoryId: number, question: Omit<QuestionType, 'category'>) {
    const category = await this.categoryDB.findFirst({ where: { id: { equals: categoryId } } });
    if (!category) {
      throw new Error('Category not found');
    }

    return await this.questionDB.create({
      data: {
        ...question,
        category: {
          connect: {
            id: categoryId,
          }
        }
      }
    });
  }

  async createAnswer(questionId: number, answer: Omit<AnswerType, 'question' | 'votes'>) {
    const question = await this.questionDB.findFirst({ where: { id: { equals: questionId } } });
    if (!question) {
      throw new Error('Question not found');
    }
    return await this.answerDB.create({
      data: {
        ...answer,
        question: {
          connect: {
            id: questionId,
          }
        }
      }
    });
  }

  async filterPaginateCategory(options: PaginateOptions, name?: string): Promise<PaginateType<CategoryType[]>> {
    let filter = {};
    if (name) {
      filter = {
        where: {
          name: { contains: name },
        },
      };
    }

    const categories = await this.categoryDB.findMany({
      ...filter,
      skip: options.skip,
      orderBy: [
        {
          createdAt: 'desc',
        },
      ],
      take: options.size,
    });
    const countResults = await this.categoryDB.count(filter);
    const totalPages = Math.ceil(countResults / options.size);
    return {
      page: options.page,
      next: options.page < totalPages ? options.page + 1 : null,
      previous: options.page > 0 ? options.page - 1 : null,
      totalPages,
      countResults,
      results: categories,
    };
  }
  // getPaginateQuestionByCategory(categoryId: Pick<CategoryEntity, 'id'>, options: PaginateOptions): Promise<PaginateType<QuestionEntity[]>>;
  // getPaginateAnswersByQuestion(questionId: Pick<CategoryEntity, 'id'>, options: PaginateOptions): Promise<PaginateType<AnswerEntity[]>>
}

import { PrismaClient } from '@prisma/client'

const prisma = new PrismaClient()

export default prisma;

import * as dotenv from 'dotenv';
if (process.env['NODE_ENV'] === 'test') {
  dotenv.config({ path: '.env.test' });
} else {
  dotenv.config({ path: '.env.dev' });
}


const keys = {
  env: process.env['NODE_ENV'],
  port: process.env['PORT'],
  mysql_url: process.env['DATABASE_URL']
};

interface Keys {
  env: 'development' | 'staging' | 'production';
  port: number;
  mysql_url: string,
}

const getSanitizedConfig = (config: typeof keys): Keys => {
  for (const [key, value] of Object.entries(config)) {
    if (value === undefined) {
      throw new Error(`Missing key ${key} in config.env`);
    }
  }
  return config as unknown as Keys;
};

export default getSanitizedConfig(keys);
